    required this.id,
    required this.picturePath,
    required this.name,
    required this.description,
    required this.ingredients,
    required this.price,
    required this.rate,
    this.types = const[]
  });
  
  @override
  // TODO: implement props
  List<Object?> get props => [id, picturePath, name, description, ingredients, price, rate];
}

